const repeatString = function(string, num) {
// hey*3
// initializer is calls the function and serves
// as counter
// Condition if init = 4 stop looping
//
    for (let i = 0; i < (num + 1); i++) {
        return (string);
    }
};  

repeatString('hey', 3);

